		SELECT 
			id,
			g_detail_a ,g_detail_b , g_code ,g_name ,g_hit, 
			g_recom ,g_new ,g_popul ,g_sale ,g_make ,
			g_brand ,g_sell ,g_memo ,g_price ,g_inven,
			g_option_a ,g_option_b ,g_image_a ,g_image_b ,
			g_count, g_goods_count ,g_ori_price , g_discount ,g_disc_price,
			g_count_price,g_point,g_cont,g_count_disprice,
			g_count_memprice,g_count_oriprice,g_count_memprice_price
		FROM goods 



                g_detail_a LIKE concat('%', #{g_detail_a}, '%')


                AND g_detail_b LIKE concat('%', #{g_detail_b}, '%')


                AND g_brand LIKE concat('%', #{g_brand}, '%')


				AND g_count_memprice_price >= ${min_price}


				AND g_count_memprice_price  <= ${max_price}



					AND g_count_memprice_price >= ${min_price}
					AND g_count_memprice_price  <= ${max_price}





					AND g_count_memprice_price  >= ${min_price}
					AND	g_count_memprice_price   <   (${min_price})+100000





					AND g_count_memprice_price >= ${min_price}




					AND g_count_memprice_price  <= ${max_price}




		ORDER BY g_count desc


		LIMIT #{offset}, #{listCount}

